import { createSlice } from '@reduxjs/toolkit';
import * as api from '../api/index';

const slice = createSlice({
    name: 'plant',
    initialState: {
        plant: {}
    },
    reducers: {
        fetchOne: (state, action) => {
            state.plant = action.payload
        },
    }
})
export default slice.reducer; 

const { fetchOne } = slice.actions; 

export const SelectSinglePlant = (state) => state.plant.plant.data; 
export const fetchOnePlant = (id) => async (dispatch) => {
    try {
        let { data } = await api.fetchOnePlant(id);
        dispatch(fetchOne({ data }));
    } catch (error) {
        console.log(error)
    }
}
export const fetchAllPlants = createAsyncThunk('plants/fetchAllPlants', async () => {
    try {
        let { data } = await api.fetchAllPlants();
        return data
    } catch (error) {

    }
})
