import React, { useEffect, useState } from 'react';
import { Button, Typography, Box, List, ListItem, TextField, IconButton, Card, CardHeader, CardContent, CardActions, Select, MenuItem} from '@mui/material';
import CloseRounded from '@mui/icons-material/CloseRounded';
import {CHARACTER_LIMIT} from '../../../constants/limits'; 

import selectedFile from '../../../models/fileClass';
import PlantSpecifics from '../../../models/plantSpecifics';
import {plantType} from '../../../constants/plantType'; 
import { nanoid } from 'nanoid';

const PlantDetails = ({plantInfo, idx, handleInputChange, handleSavePlant, handleDeletePlantFromList}) => {
    let plantSpecificsObject = new PlantSpecifics("", 0, "", []);

    const [plantDetails, setPlantDetails] = useState(plantSpecificsObject);
    const [uniqueID, setUniqueID] = useState(nanoid())

    // useEffect(()=>{
    //         localStorage.setItem(`plantDetails:${idx}`, JSON.stringify(plantDetails))
    // });

    // function handleInputChange(event) {
    //     let newObj = {};
    //     if (!isNaN(parseInt(event.target.value))) {
    //         newObj[event.target.name] = parseInt(event.target.value);
    //     } else {
    //         newObj[event.target.name] = event.target.value;
    //     }
    //     console.log(plantDetails, idx)
    //     setPlantDetails({
    //         ...plantDetails,
    //         ...newObj
    //     })
    // };

    const toBase64 = file => new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => resolve(reader.result);
        reader.onerror = error => reject(error);
    });

    async function handleFileSelection(event) {
        let files = event.target.files;
        let tempArray = [];

        if (files) {
            for (let i = 0; i < files.length; i++) {
                let selectedFileObj = new selectedFile(files[i].name, files[i].type, files[i].size, await toBase64(files[i]));
                tempArray.push(selectedFileObj);
            };
        }
        setPlantDetails({ ...plantDetails, selectedFiles: tempArray })
    }

    function DeleteFileFromList(event) {
        let filteredList = plantDetails.selectedFiles.filter(file => file.name !== event.currentTarget.value);
        setPlantDetails({ ...plantDetails, selectedFiles: [...filteredList] });
    }
    function DeletePlantCard(){
        localStorage.removeItem(`plantDetails:${idx}`)
        handleDeletePlantFromList()
    }

    function handleClearForm() {
        setPlantDetails(plantSpecificsObject)
    }

    return(
        <>
            <Card sx={{ maxWidth: 345, backgroundColor: '#cfd1d0', margin: '1rem .5rem'}}>
                <CardActions disableSpacing>
                    <IconButton onClick={DeletePlantCard}>
                        <CloseRounded />
                    </IconButton>
                </CardActions>
                <CardHeader 
                    title={`Plant #${idx + 1}`}
                />
                <CardContent>
                    <Box>
                        <Select 
                            value={plantInfo.type}
                            label="Type"
                            name="type"
                            onChange={(e)=>handleInputChange(e, 'list')}>
                            <MenuItem value="">--</MenuItem>
                            {plantType.map(type => <MenuItem value={type} key={plantType.indexOf(type)}>{type}</MenuItem>)}
                        </Select>
                    </Box>
                    <Box>
                        <TextField
                            label="Price"
                            name="price"
                            fullWidth
                            inputProps={{ inputMode: 'numeric', pattern: '[0-9]*'}}
                            value={plantInfo.price}
                            variant="outlined"
                            onChange={(e) => handleInputChange(e, 'list')}
                        />
                    </Box>
                    <Box>
                        <TextField
                            label="Description"
                            name="description"
                            fullWidth
                            multiline
                            maxRows={4}
                            inputProps={{ maxLength: CHARACTER_LIMIT }}
                            value={plantInfo.description}
                            variant="outlined"
                            onChange={(e) => handleInputChange(e, 'list', idx)}
                        />
                    </Box>
                    <Box>
                        <input
                            style={{ display: "none" }}
                            name="selectedFiles"
                            id={uniqueID}
                            type="file"
                            multiple
                            onChange={handleFileSelection}
                        />
                        <label htmlFor={uniqueID}>
                            <Button variant="contained" component="span">
                                Upload Image(s)
                                </Button>
                        </label>
                        <Box>
                            <List>
                                {plantInfo.selectedFiles.map(file => <ListItem key={plantInfo.selectedFiles.indexOf(file)}>
                                    <Typography variant="subtitle2">{`${file.name.slice(0, 10)}...${file.name.slice(file.name.length - 4, file.name.length)}`}</Typography>
                                    <Button
                                        value={file.name}
                                        onClick={DeleteFileFromList}
                                    >
                                        <CloseRounded />
                                    </Button>
                                </ListItem>)}
                            </List>
                        </Box>
                    </Box>
                </CardContent>
                <CardActions>
                    <Button onClick={handleSavePlant}>Save</Button>
                    <Button onClick={handleClearForm}>Clear</Button>
                </CardActions>
            </Card>                       
                        </>
    )
}

export default PlantDetails;
