import {createAsyncThunk, createSlice} from '@reduxjs/toolkit';
import * as api from '../api/index'; 

//Slice 

const slice = createSlice({
    name: 'plants', 
    initialState: {
        plants: [],
        status: 'idle', //'idel' | 'loading' | 'succeeded' | 'failed'
        error: null
    }, 
    reducers: {
        fetchAll: (state, action) =>{
            state.plants = action.payload; 
        }, 
        createPlant: (state, action) =>{
            const newPlantList = state.plants.push(action.payload)
            state.plants = newPlantList
        }, 
        deleteOne: (state, action) =>{
            state.plants  = state.plants.filter(plant => plant._id !== action.payload._id)

        }
    }, 
    extraReducers(builder){
        builder
            .addCase(fetchAllPlants.pending, (state, action)=>{
                state.status = 'loading'; 
                console.log()
            })
            .addCase(fetchAllPlants.fulfilled, (state, action)=>{
                state.status = 'succeeded'
                console.log(action.payload)
                state.plants = state.plants.concat(action.payload)
            })
            .addCase(fetchAllPlants.rejected, (state, action) => {
                state.status = 'failed'
                state.error = action.error.message
            })
    }
}); 


export default slice.reducer; 

// Actions
const {fetchAll, createPlant, deleteOne} = slice.actions;

export const SelectAllPlants = (state) => state.plants.plants.data

export const createNewPlant = (plantInfo) => async (dispatch)=>{
    try{
        let {data} = await api.addNewPlant(plantInfo); 
        dispatch(createPlant({data}))
    }catch(error){
        console.log(error)
    }
}; 

export const fetchAllPlants = createAsyncThunk('plants/fetchAllPlants', async () =>{
    try {
        let { data } = await api.fetchAllPlants();
        return [...data]
    } catch (error) {

    }
})

