import React from 'react';
import {useState} from 'react'; 
// Material UI Section 
import { Button, Typography, Paper, Box, Input, InputLabel, Select, MenuItem} from '@mui/material';
// Other imports 
import plantCategories from '../../lists/plantCategories';
import sizes from '../../constants/sizes'; 
let blankState = {name: "", category: "", quantity: 0, size: "", description: ""}
const FormAddPlant = (e) => {
    // Component State section 
    const [plantInfo, setPlantInfo] = useState(blankState); 
    const [formValid, setFormValid] = useState(true)
    console.log(plantInfo)
    function handleInputChange(event){
        let newObj = {}; 
        newObj[event.target.name] = event.target.value; 
        setPlantInfo({
            ...plantInfo, 
            ...newObj
        })
    }

    function handleSubmit(e){
        // Prevent postback 
        e.preventDefault(); 
        if(submitValidation()){
            setFormValid(false); 
            console.log("submitted")
        }else{
            setFormValid(true)
            console.log("not valid")
        }

    }

    function handleClearForm(){
        setPlantInfo(blankState)
    }

    function submitValidation(){
        return (plantInfo.name.trim() !== "" && plantInfo.category.trim() !== "" && plantInfo.quantity !== 0 && plantInfo.size.trim() !== "" && plantInfo.description.trim()  !== "")
    }
    return (
        <Paper>
            <form autoComplete="off" noValidate onSubmit={(e) => handleSubmit(e)}>
                <Box>
                    <Typography>Describe the new member!</Typography>
                </Box>
                <Box>
                    {!formValid && <Typography color="error">* All fields are required.</Typography>}
                </Box>
                <Box>
                    <Box>
                        <InputLabel>Name</InputLabel>
                        <Input name="name" type="text" value={plantInfo.name} onChange={handleInputChange}></Input>
                    </Box>
                    <Box>
                        <InputLabel>Category</InputLabel>
                        <Select label="Category" name="category" value={plantInfo.category} onChange={handleInputChange}>
                            <MenuItem> -- </MenuItem>
                            {
                                plantCategories.map(category => <MenuItem key={plantCategories.indexOf(category)} value={category}>{category}</MenuItem>)
                            }
                        </Select>
                    </Box>
                    <Box>
                        <InputLabel>Quantity</InputLabel>
                        <Input name="quantity" type="number" value={plantInfo.quantity} onChange={handleInputChange}></Input>
                    </Box>
                    <Box>
                        <InputLabel>Size</InputLabel>
                        <Select label="Size" name="size" value={plantInfo.size} onChange={handleInputChange}>
                            <MenuItem> -- </MenuItem>
                            {
                                sizes.map(size => <MenuItem key={sizes.indexOf(size)} value={size}>{size}</MenuItem>)
                            }
                        </Select>
                    </Box>
                    <Box>
                        <InputLabel>Description</InputLabel>
                        <Input name="description" type="text" value={plantInfo.description} onChange={handleInputChange}></Input>
                    </Box>
                </Box>
                <Box>
                    <Button variant="contained" size="large" type="submit">Add</Button>
                    <Button variant="contained" color="error" size="large" onClick={handleClearForm}>Clear</Button>
                </Box>
            </form>
        </Paper>
    )
}

export default FormAddPlant;
