import React, {useEffect, useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
// Redux store functions
import { fetchOnePlant } from '../../store/plants';
// Meterial UI Section
import {FormControl, Button, Typography, Card, CardMedia, CardContent, CardActions, Box, Input, InputLabel, MenuItem, Select} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import ArrowLeftIcon from '@mui/icons-material/ArrowLeft';
// Other Imports 
import plantCategories from '../../lists/plantCategories';
import sizes from '../../constants/sizes';
const PlantCard = () => { 
    const { id } = useParams();
    const plant = useSelector((state) => state.plants.plant.data);
    const [plantInfo, setPlantInfo] = useState({ name: "", category: "", quantity: 0, size: "", description: "", price: 0, selectedFiles: [] }); 
    const [editMode, setEditMode] = useState(false); 
    const dispatch = useDispatch(); 

    useEffect(()=>{
        dispatch(fetchOnePlant(id))

    },[id])

    function handleInputChange(){

    }
    function handleEditMode(){
        setEditMode(!editMode)
    }
    return (

            <Card sx={{margin: "10px"}}>          
                <CardMedia component="img" height="200"  image={`${plant?.selectedFiles[0].base64}`} />
                    { editMode ? (
                        <CardContent>
                            <Box>
                                <FormControl>

                                </FormControl>

                            </Box>
                        </CardContent>
                    ) : (
                    <CardContent>
                        <Typography variant='h4'>{plant?.name}</Typography>
                        <Typography><b>Category: </b>{plant?.category}</Typography>
                        <Typography><b>Size: </b>{plant?.size}</Typography>
                        <Typography><b>Status: </b>{plant?.status}</Typography>
                        <Typography><b>Price: </b>${plant?.price}</Typography>
                    </CardContent>
                    )}               
                <CardActions>
                    { editMode ? (
                        <Box>
                            <Button onClick={handleEditMode}>Save</Button>
                            <Button color="warning" onClick={handleEditMode}>Cancel</Button>
                        </Box>                       
                    ) : (
                        <Box>
                            <Link to="/plants" replace><ArrowLeftIcon /></Link>
                            <Button onClick={handleEditMode}>
                                <EditIcon />
                            </Button>
                            <Button color="warning"><DeleteForeverIcon /></Button>
                        </Box>                     
                    )}  
                </CardActions> 
            </Card>


    )
}

export default PlantCard;
