import React, {useEffect} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchOnePlant } from '../../store/plants';
import { Button, Typography, Card, CardHeader, CardMedia, CardContent} from '@mui/material';
import { useParams } from 'react-router-dom';
const PlantCard = () => { 
    const { id } = useParams();
    const plant = useSelector((state) => state.plants.plant.data); 
    const dispatch = useDispatch(); 

    useEffect(()=>{
        dispatch(fetchOnePlant(id))

    },[id])

    return (

            <Card sx={{margin}}>          
                <CardMedia component="img" height="200" image={`${plant?.selectedFiles[0].base64}`} />
                <CardContent>
                    <Typography variant='h4'>{plant?.name}</Typography>
                    <Typography>{plant?.category}</Typography>
                    <Typography>{plant?.size}</Typography>
                    <Typography>{plant?.status}</Typography>
                    <Typography>${plant?.price}</Typography>
                </CardContent>

            </Card>


    )
}

export default PlantCard;
