import React from 'react';
import { Button, Typography, Box} from '@mui/material';
import { useSelector } from 'react-redux';
import FormAddPlant from './FormAddPlant.js'
const Plants = () => {
    const plantList = useSelector((state) => state.plants.plants.data)
    console.log(plantList, ' plant list')
    return (
        <div>
            <Box> 
                {plantList && plantList.map((plant)=> <Typography>{plant.name}</Typography>)}
            </Box>
            <Box sx={{margin: '1rem 0', backgroundColor: 'whitesmoke'}}>
                <Button>Add a Plant</Button>
            </Box>
            <FormAddPlant />
        </div>
    )
}

export default Plants;
