import React from 'react';
import { Link } from 'react-router-dom';
import { Button, Typography, ListItem, Badge, Box, Paper} from '@mui/material';
import PreviewIcon from '@mui/icons-material/Preview';
import PlantListItem from './PlantListItem';

const PlantSection = ({ plantSection }) => {

    return (
        <Paper elevation={3} sx={{ backgroundColor: 'whitesmoke'}}>
            <Box sx={{marginBottom: '1rem', textAlign: 'center'}} >
                <Typography variant="h4">{plantSection.name}</Typography>
                <Typography variant="caption" variant="h6">{plantSection.category}</Typography>
            </Box>
            {
                plantSection.multiple && (
                    plantSection.multiple.map(plant => <PlantListItem plant={plant} />)
                )
            }
       </Paper>
    )
}

export default PlantSection;
