import React from 'react';
import { Button, Typography, Box } from '@mui/material';

const FormConfirmAddPlant = ({plantInfo, handleSubmit}) => {
    return (
        <Box sx={{ backgroundColor: '#DFF0DA'}}>
            <Box>
                <Typography>Name: {plantInfo.name}</Typography>
                <Typography>Category: {plantInfo.category}</Typography>
                <Typography>Qty: {plantInfo.quantity}</Typography>
                <Typography>Size: {plantInfo.size}</Typography>
                <Typography>Description: {plantInfo.description}</Typography>
                <Typography>${plantInfo.price}</Typography>
            </Box>
            <Box>
                <Button>Confirm</Button>
                <Button>Edit</Button>
            </Box>
        </Box>
    )
}

export default FormConfirmAddPlant;
