import React from 'react';
import { Link } from 'react-router-dom';
import { Button, Typography, ListItem, Badge, Box, Card, CardHeader, CardActionArea, CardActions, CardContent, CardMedia} from '@mui/material';
import PreviewIcon from '@mui/icons-material/Preview';

const PlantListItem = ({ plant }) => {

    return (
       <ListItem >
            <Card sx={{width: '100%'}}>
                <CardHeader
                title={plant.type}
                subheader={`$${plant.price}`}>
                </CardHeader>
                <CardMedia
                    component="img"
                    height="194"
                    image={`${plant.selectedFiles[0].base64}`}
                    alt="Paella dish"
                />
                <CardContent>
                    <Typography variant="body2" text></Typography>
                </CardContent>

            </Card>
            <Box sx={{width: '100%'}}>
                <Typography sx={{ marginRight: '5px' }}>{plant.description}</Typography>
                <Typography variant="caption">${plant.price}</Typography><br></br>
                <Typography variant="caption">{plant.selectedFiles.length}</Typography>
                {/* <Button><Link to={`/plant-details/${plant._id}`} replace><PreviewIcon /></Link></Button> */}
            </Box>
            <Badge badgeContent={plant.quantity} color="success" ></Badge>
       </ListItem>
    )
}

export default PlantListItem;
