import React, {useEffect, useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { useParams, useNavigate} from 'react-router-dom';
// Redux store functions
import { deletePlant, editPlant} from '../../store/plantsSlice';
import {fetchOnePlant, SelectSinglePlant, getPlantStatus, getPlantError} from '../../store/plantSlice'; 
// Meterial UI Section
import {FormControl, Button, Typography, Card, CardMedia, CardContent, CardActions, Box, Input, InputLabel, MenuItem, Select} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import ArrowLeftIcon from '@mui/icons-material/ArrowLeft';
// Other Imports 
import plantCategories from '../../lists/plantCategories';
import sizes from '../../constants/sizes';
import statuses from '../../constants/status'; 
import FormConfirmDelete from './FormConfirmDelete';
import { PlantObjectValidator } from '../../services/validation';
const PlantCard = () => { 
    const { id } = useParams();
    const plant = useSelector(SelectSinglePlant); 
    const plantStatus = useSelector(getPlantStatus)
    const [plantInfo, setPlantInfo] = useState({});
    const [editMode, setEditMode] = useState(false); 
    const [formConfirmDeleteVisible, setFormConfirmDeleteVisible] = useState(false); 
    const [formHasAllRequiredFields, setFormHasAllRequiredFields] = useState(true);
    const [inputOfNumberValid, setInputOfNumberValid] = useState(true);
    const dispatch = useDispatch(); 
    const navigate = useNavigate(); 

    useEffect(() => {

        dispatch(fetchOnePlant(id))
    }, [id]); 
    useEffect(()=>{
        setPlantInfo(plant)
    }, [plant]); 

    function handleInputChange(event){
        let newObj = {};
        if (!isNaN(parseInt(event.target.value))) {
            newObj[event.target.name] = parseInt(event.target.value);
        } else {
            newObj[event.target.name] = event.target.value;
        }
        setPlantInfo({
            ...plantInfo,
            ...newObj
        })
    }
    function handleEditMode(){
        setEditMode(!editMode);  
    }; 

    function handleSaveEdit(){
        let PlantValidator = new PlantObjectValidator(plantInfo)
        let result = PlantValidator.validate(); 
        setFormHasAllRequiredFields(PlantValidator.hasAllRequiredFields);
        setInputOfNumberValid(PlantValidator.inputNumberIsValid);
        if(result){
            dispatch(editPlant(plantInfo));
            navigate('/plants')
        } 
    }; 

    function handleDeleteItem(){
        dispatch(deletePlant(id)); 
        navigate('/plants')
    }; 

    return (
            <Box>
            {formConfirmDeleteVisible && <FormConfirmDelete handleDeleteItem={handleDeleteItem} setFormConfirmDeleteVisible={setFormConfirmDeleteVisible} /> }
                <Card sx={{ margin: "10px" }}>
                    {editMode ? (
                        <CardContent>
                            <>
                                <input type="file" id="file" style={{ display: 'none' }} />
                                <CardMedia component="img" height="200" image={`${plant?.selectedFiles[0].base64}`} />
                            </>
                            <Box>
                                {!formHasAllRequiredFields && <Typography color="error">* All fields and images are required.</Typography>}
                                {!inputOfNumberValid && <Typography color="error">* Quantity or Price is not a valid number.</Typography>}
                            </Box>
                            <Box>
                                <FormControl fullWidth>
                                    <InputLabel id="name">Name</InputLabel>
                                    <Input name='name' type="text" id="name" value={plantInfo?.name} sx={{ display: 'block' }} placeholder={`Name: ${plant?.name}`} onChange={handleInputChange}></Input>
                                </FormControl>
                                <Box>
                                    <FormControl fullWidth>
                                        <InputLabel id="category">Category</InputLabel>
                                        <Select label="Category" name="category" id="category" value={plantInfo?.category} sx={{ color: 'black' }} onChange={handleInputChange}>
                                            <MenuItem value=""> -- </MenuItem>
                                            {plantCategories.map(category => <MenuItem key={plantCategories.indexOf(category)} value={category}>{category}</MenuItem>)}
                                        </Select>
                                    </FormControl>
                                </Box>
                                <Box>
                                    <FormControl fullWidth>
                                        <InputLabel id="size">Size</InputLabel>
                                        <Select label="Size" name="size" id="size" value={plantInfo?.size} onChange={handleInputChange}>
                                            <MenuItem value=""> -- </MenuItem>
                                            {sizes.map(size => <MenuItem key={sizes.indexOf(size)} value={size}>{size}</MenuItem>)}
                                        </Select>
                                    </FormControl>
                                </Box>
                                <Box>
                                    <FormControl fullWidth>
                                        <InputLabel id="status">Status</InputLabel>
                                        <Select label="status" name="status" id="status" value={plantInfo?.status} onChange={handleInputChange}>
                                            <MenuItem value=""> -- </MenuItem>
                                            {statuses.map(status => <MenuItem key={statuses.indexOf(status)} value={status}>{status}</MenuItem>)}
                                        </Select>
                                    </FormControl>
                                </Box>
                                <FormControl fullWidth>
                                    <InputLabel id="price">Price</InputLabel>
                                    <Input name='price' type="number" id="price" value={plantInfo?.price} sx={{ display: 'block' }} placeholder={`Price: $${plantInfo?.price}`} onChange={handleInputChange}></Input>
                                </FormControl>
                            </Box>
                        </CardContent>
                    ) : (
                        <CardContent>
                                            <CardMedia component="img" height="200" image={`${plant?.selectedFiles[0].base64}`} />
                            <Typography variant='h4'>{plant?.name}</Typography>
                            <Typography><b>Category: </b>{plant?.category}</Typography>
                            <Typography><b>Size: </b>{plant?.size}</Typography>
                            <Typography><b>Status: </b>{plant?.status}</Typography>
                            <Typography><b>Price: </b>${plant?.price}</Typography>
                        </CardContent>
                    )}
                    <CardActions>
                        {editMode ? (
                            <Box>
                            <Button onClick={handleSaveEdit}>Save</Button>
                                <Button color="warning" onClick={handleEditMode}>Cancel</Button>
                            </Box>
                        ) : (
                            <Box>
                                <Link to="/plants" replace><ArrowLeftIcon /></Link>
                                <Button onClick={handleEditMode}>
                                    <EditIcon />
                                </Button>
                                    <Button color="warning" onClick={()=>setFormConfirmDeleteVisible(true)}><DeleteForeverIcon /></Button>
                            </Box>
                        )}
                    </CardActions>
                </Card>
            </Box>



    )
}

export default PlantCard;
