import {createSlice} from '@reduxjs/toolkit'; 
import * as api from '../api/index'; 
//Slice 

const slice = createSlice({
    name: 'plants', 
    initialState: [], 
    reducers: {
        fetchAll: (state, action) =>{
            state.plants = action.payload; 
        }, 
        fetchOne: (state, action) =>{
            state.plant = action.payload
        },
        createPlant: (state, action) =>{
            const newPlantList = state.plants.push(action.payload)
            state.plants = newPlantList
        }, 
        deleteOne: (state, action) =>{
            state.plants  = state.plants.filter(plant => plant._id !== action.payload._id)

        }
    }
}); 

export default slice.reducer; 

// Actions
const {fetchAll, fetchOne, createPlant, deleteOne} = slice.actions; 

export const createNewPlant = (plantInfo) => async (dispatch)=>{
    try{
        let {data} = await api.addNewPlant(plantInfo); 
        dispatch(createPlant({data}))
    }catch(error){
        console.log(error)
    }
}; 

export const fetchAllPlants = () => async (dispatch)=>{
    try {
        let {data} = await api.fetchAllPlants(); 
        dispatch(fetchAll({data}))
    } catch (error) {

    }
}

export const fetchOnePlant = (id) => async (dispatch) =>{
    try{
        let {data} = await api.fetchOnePlant(id); 
        dispatch(fetchOne({data})); 
    } catch(error){
        console.log(error)
    }
}

export const deleteOnePlant = (id) => async (dispatch) =>{
    try {
        let {data} = await api.deleteOnePlant(id);
        dispatch(deleteOne({data}))
    } catch (error) {
        console.log(error)
    }
}
